//Hack to fix storybook webpack builds since the obsidian package doesn't include source code
const obsidian = {
    requireApiVersion(version) {
        if (version === '15.0.0') {
            return true
        }
        else if (version === '16.0.0') {
            return false
        }
        throw new Error("Unhandled version in requireApiVersion mock: " + version)
    }
}

module.exports = obsidian